mapper.insert:
INSERT INTO coffee_tbl
    (name
    , price
    , source
    , shots)
VALUES
    (#{name}
    , #{price}
    , #{source}
    , #{shots})

mapper.update:
UPDATE
    coffee_tbl
SET
    id = #{id}
  , name = #{name}
  , price = #{price}
  , source = #{source}
  , shots = #{shots}
WHERE
    id = #{id}
