mapper.insert:
INSERT INTO
    user_tbl(id
            , user
            , user_name
            , password
            , email
            , phone_number
            , role
            , max_lock)
VALUES
    ( #{id}
    , #{userName}
    , #{password}
    , #{email}
    , #{phoneNumber}
    , #{role}
    , #{maxLock} )

mapper.update:
UPDATE
    user_tbl
SET
  user_name = #{userName}
  , password = #{password}
  , email = #{email}
  , phone_number = #{phoneNumber}
  , role = #{role}
  , max_lock = #{maxLock}
WHERE
    id = #{id}
